(function(){tinymce.PluginManager.requireLangPack('wikindxCite');tinymce.create('tinymce.plugins.WikindxCitePlugin',{init:function(ed,url){ed.addCommand('mceWikindxCite',function(){ed.windowManager.open({file:url+'/dialog.php',width:780+parseInt(ed.getLang('wikindxCite.delta_width',0)),height:720+parseInt(ed.getLang('wikindxCite.delta_height',0)),inline:1,scrollbars:'yes'},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('wikindxCite',{title:'wikindxCite.desc',cmd:'mceWikindxCite',image:url+'/img/wikindxCite.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('wikindxCite',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'WikindxCite plugin',author:'Mark Grimshaw-Aagaard',authorurl:'https://wikindx.sourceforge.io',infourl:'',version:"1.1"}}});tinymce.PluginManager.add('wikindxCite',tinymce.plugins.WikindxCitePlugin)})();