(function(){tinymce.PluginManager.requireLangPack('wikindxTable');tinymce.create('tinymce.plugins.WikindxTablePlugin',{init:function(ed,url){ed.addCommand('mceWikindxTable',function(){ed.windowManager.open({file:url+'/dialog.php',width:400,height:200,inline:1,scrollbars:'yes'},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('wikindxTable',{title:'wikindxTable.desc',cmd:'mceWikindxTable',image:url+'/img/wikindxTable.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('wikindxTable',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'WikindxTable plugin',author:'Mark Grimshaw-Aagaard',authorurl:'https://wikindx.sourceforge.io',infourl:'',version:"1.1"}}});tinymce.PluginManager.add('wikindxTable',tinymce.plugins.WikindxTablePlugin)})();